import React, { useState } from 'react';
import './index.css';
import { Link } from "react-router-dom";
import cricketLogo from './images/5492.jpg';

const HomePage = () => {
    const [team1, setTeam1] = useState('');
    const [team2, setTeam2] = useState('');
    const [team1Name, setTeam1Name] = useState('');
    const [team2Name, setTeam2Name] = useState('');

    const selectTeam = (teamImg, teamName) => {
        if (!team1) {
            setTeam1(`${teamImg}.jpg`);
            setTeam1Name(teamName);
        } else if (!team2) {
            if (teamImg === team1) {
                alert("Please select a different team for prediction.");
            } else {
                setTeam2(teamImg);
                setTeam2Name(teamName);
            }
        } else {
            alert("You have already selected two teams. Please reset to choose again.");
        }
    };

    const clearSelections = () => {
        setTeam1('');
        setTeam2('');
        setTeam1Name('');
        setTeam2Name('');
    };

    const makePrediction = () => {
        const winPercentage = {
            csk: { mi: 40, rcb: 65.63, dc: 60, kkr: 55, pbks: 58, rr: 55, srh: 57, lsg: 50, gt: 33 },
            mi: { csk: 54.05, dc: 54.28, kkr: 67.64, pbks: 53.12, rr: 51.72, rcb: 57.58, srh: 56.52, lsg: 16.67, gt: 40 },
        };

        // Logic to calculate prediction
        const prediction = winPercentage[team1Name.toLowerCase()]?.[team2Name.toLowerCase()];
        alert(`Prediction for ${team1Name} vs ${team2Name}: ${prediction ? `${prediction}%` : 'No data available'}`);

